mapper.select:
        SELECT 

        FROM 
        	`sys_city` a


        		AND a.name = #{name}


        		AND a.province_id = #{provinceId}


        		AND a.city_no = #{cityNo}


        		AND a.full_spell = #{fullSpell}


        		AND a.first_spell = #{firstSpell}



			LIMIT #{first},#{max}
